mapper.select:
select * from movie 

	where genre like #{keyword} or grade like #{keyword} or actor like #{keyword}


	where genre like #{keyword}


	where grade like #{keyword}


	where actor like #{keyword}

order by num desc

select count(*) from movie

	where genre like #{keyword} or grade like #{keyword} or actor like #{keyword}


	where genre like #{keyword}


	where grade like #{keyword}


	where actor like #{keyword}
